import axios from "axios";
import "./App2.css";

import imgLogo from "./assets/logo.png";
import React, { useEffect, useState } from "react";

function App() {
  const [result, setResult] = useState("");
  const [buttonState, setButtonState] = useState({
    button1: false,
    button2: false,
    button3: false,
    button4: false,
  });

  const handleButtonClick = (value) => {
    setResult(value);
    console.log(buttonState);
  };

  const toggleButton = (button) => {
    setButtonState((prevState) => ({
      ...prevState,
      [button]: !prevState[button],
    }));
  };

  const [rice, setRice] = useState(false);
  const [side, setSide] = useState(false);
  const [soup, setSoup] = useState(false);
  const [dessert, setDessert] = useState(false);
  const [selectFoodData, setSelectFoodData] = useState();
  const buttonData = async () => {
    console.log(
      "버튼상태=>" +
        buttonState.button1 +
        buttonState.button2 +
        buttonState.button3 +
        buttonState.button4
    );

    if (buttonState.button1) {
      console.log("밥은 트루임");
      setRice(buttonState.button1);
    } else {
      setRice(false);
    }
    if (buttonState.button2) {
      console.log("반찬은 트루임");
      setSide(buttonState.button2);
    } else {
      setSide(false);
    }
    if (buttonState.button3) {
      console.log("국은 트루임");
      setSoup(buttonState.button3);
    } else {
      setSoup(false);
    }
    if (buttonState.button4) {
      console.log("디저트는 트루임");
    } else {
      setDessert(false);
    }

    const updateAndLogState = (
      buttonStateValue,
      setStateFunction,
      stateName
    ) => {
      setStateFunction(buttonStateValue, () => {
        console.log("음식적용테스트 =>", rice, side, soup, dessert);
      });
    };

    updateAndLogState(buttonState.button1, setRice, "rice");
    updateAndLogState(buttonState.button2, setSide, "side");
    updateAndLogState(buttonState.button3, setSoup, "soup");
    updateAndLogState(buttonState.button4, setDessert, "dessert");
    //console.log("음식적용테스트 =>" + rice + side + soup + dessert);

    try {
      // Axios를 사용하여 Node.js로 데이터 전송
      const response = await axios.post("/testdb/selectFoodData", {
        rice,
        side,
        soup,
        dessert,
      });

      setSelectFoodData(response.data); // Node.js에서 받은 데이터를 상태에 저장
      console.log(JSON.stringify(response.data));
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const [foodData, setFoodData] = useState();
  const getFoodData = async () => {
    try {
      axios.get("/testdb/getAllFood").then((res) => {
        //console.log(res.data);
        setFoodData(res.data);
      });
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    getFoodData();
    console.log(JSON.stringify(foodData));
  }, []);

  return (
    <div>
      <div className="top">
        <img src={imgLogo} />
        <h1>단웅셰프</h1>
      </div>
      <div className="search-prompt">
        <input
          type="text"
          placeholder="원하는 재료를 검색해주세요."
          className="search-input"
        />
        <img src="" />
      </div>
      <div className="contents-container">
        <div className="filter-container">
          <h4>카테고리</h4>
          <button
            onClick={() => {
              handleButtonClick("결과 1");
              toggleButton("button1");
            }}
            className={buttonState.button1 ? "activeButton" : "inactiveButton"}
          >
            밥
          </button>
          <button
            onClick={() => {
              handleButtonClick("결과 2");
              toggleButton("button2");
            }}
            className={buttonState.button2 ? "activeButton" : "inactiveButton"}
          >
            반찬
          </button>
          <button
            onClick={() => {
              handleButtonClick("결과 3");
              toggleButton("button3");
            }}
            className={buttonState.button3 ? "activeButton" : "inactiveButton"}
          >
            국
          </button>
          <button
            onClick={() => {
              handleButtonClick("결과 4");
              toggleButton("button4");
            }}
            className={buttonState.button4 ? "activeButton" : "inactiveButton"}
          >
            후식
          </button>
          <h4>영양 성분</h4>
          <div className="nutrient-filter">
            <div className="prompt">
              <label for="carbohydrate" className="car">
                탄수화물
              </label>
              <input
                type="text"
                id="carbohydrate"
                name="carbohydrate"
                placeholder="0"
              />
              <span>이상</span>
              <input
                type="text"
                id="carbohydrate"
                name="carbohydrate"
                placeholder="0"
              />
              <span>이하</span>
            </div>
            <div className="prompt">
              <label for="protein" className="pro">
                단백질
              </label>
              <input type="text" id="protein" name="protein" placeholder="0" />
              <span>이상</span>
              <input type="text" id="protein" name="protein" placeholder="0" />
              <span>이하</span>
            </div>
            <div className="prompt">
              <label for="lipid" className="lip">
                지방
              </label>
              <input type="text" id="lipid" name="lipid" placeholder="0" />
              <span>이상</span>
              <input type="text" id="lipid" nam="lipid" placeholder="0" />
              <span>이하</span>
            </div>
            <div className="prompt">
              <label for="sodium" className="sod">
                나트륨
              </label>
              <input type="text" id="sodium" name="sodium" placeholder="0" />
              <span>이상</span>
              <input type="text" id="sodium" name="sodium" placeholder="0" />
              <span>이하</span>
            </div>
          </div>
          <button className="search-button" onClick={buttonData}>
            검색
          </button>
        </div>
        <div className="search-results">
          <span>내용이 들어올 예정</span>
          {!foodData ? null : (
            <>
              {foodData.map((item) => (
                <li key={item.id}>
                  <img src={item.main_image} alt="Example Image" />
